import { Component, OnInit } from '@angular/core';
import { GiphyService } from 'src/app/services/giphy.service';

@Component({
  selector: 'app-buscador',
  templateUrl: './buscador.component.html',
  styleUrls: ['./buscador.component.css']
})
export class BuscadorComponent  implements OnInit{

  gifs: any;
  searchQuery!: string;
  notFound = false;
  limit = 8;
  searchHistory: string[] = [];
  showSearchHistory: boolean = false;

  constructor(private service: GiphyService) {}

  ngOnInit() {}

  searchGifs() {
    this.service.searchGifs(this.searchQuery)
      .subscribe(results => {
        this.gifs = results.data.slice(0, this.limit);
        console.log(this.gifs);
        if (this.searchQuery && !this.searchHistory.includes(this.searchQuery)) {
          this.searchHistory.push(this.searchQuery);
        }
      }, error => {
        console.error('Error al buscar gifs:', error);
        this.notFound = true;
      });
    this.searchQuery = '';
  }

  addToSearchHistory(query: string) {
    if (!this.searchHistory.includes(query)) {
      this.searchHistory.push(query);
    }
  }

  clearSearchHistory() {
    this.searchHistory = [];
  }

  searchInputFocus() {
    this.showSearchHistory = true;
  }

  searchInputBlur() {
    // Agregar un pequeño retraso para asegurarse de que el historial de búsqueda se muestre correctamente
    setTimeout(() => {
      this.showSearchHistory = false;
    }, 200);
  }




}
<p>Buscar:</p>
<input type="text" [(ngModel)]="searchQuery" placeholder="Buscar gifs...">
<button (click)="searchGifs()">Buscar</button>

<div class="grid-container">
    <div class="card-body" *ngFor="let gif of gifs">
      <div class="imagen">
        <img [src]="gif.images.original.url" alt="">
      </div>
      <div class="nombre">
        {{ gif.title }}
      </div>
    </div>
  </div>

