import { Component, OnInit } from '@angular/core';
import { GiphyService } from 'src/app/services/giphy.service';

@Component({
  selector: 'app-buscador',
  templateUrl: './buscador.component.html',
  styleUrls: ['./buscador.component.css']
})
export class BuscadorComponent implements OnInit {

  gifs: any;
  searchQuery: string = '';
  notFound = false;
  limit = 8;
  searchHistory: string[] = [];

  constructor(private service: GiphyService) { }

  ngOnInit() { }

  searchGifs() {
    if (this.searchQuery.trim() !== '') {
      this.service.searchGifs(this.searchQuery)
        .subscribe(results => {
          this.gifs = results.data.slice(0, this.limit);
          console.log(this.gifs);
          this.addToHistory(); // Agregar la búsqueda al historial
          console.log('Historial de búsqueda:', this.searchHistory); // Imprimir el historial de búsqueda
        }, error => {
          console.error('Error al buscar gifs:', error);
          this.notFound = true;
        });
      this.searchQuery = ''; // Limpiar el campo de búsqueda después de la búsqueda
    }
  }

  addToHistory() {
    console.log('Agregando término al historial:', this.searchQuery);
    if (!this.searchHistory.includes(this.searchQuery.trim())) {
      this.searchHistory.unshift(this.searchQuery.trim());
      if (this.searchHistory.length > 5) {
        this.searchHistory.pop();
      }
    }
    console.log('Historial de búsqueda:', this.searchHistory);
  }

  clearHistory() {
    this.searchHistory = []; // Limpiar todo el historial
    localStorage.removeItem('searchHistory'); // Eliminar el historial almacenado en localStorage
  }
}
<p>Buscar:</p>
<input type="text" [(ngModel)]="searchQuery" list="searchHistory" placeholder="Buscar gifs...">
<button (click)="searchGifs()">Buscar</button>

<datalist id="searchHistory">
  <option *ngFor="let term of searchHistory" [value]="term"></option>
</datalist>

<div class="grid-container">
  <div class="card-body" *ngFor="let gif of gifs">
    <div class="imagen">
      <img [src]="gif.images.original.url" alt="">
    </div>
    <div class="nombre">
      {{ gif.title }}
    </div>
  </div>
</div>

<div *ngIf="searchHistory.length > 0">
  <h2>Historial de Búsqueda</h2>
  <ul>
    <li *ngFor="let term of searchHistory">{{ term }}</li>
  </ul>
  <button (click)="clearHistory()">Limpiar Historial</button>
</div>


