import { Component, OnInit } from '@angular/core';
import { GiphyService } from 'src/app/services/giphy.service';

@Component({
  selector: 'app-buscador',
  templateUrl: './buscador.component.html',
  styleUrls: ['./buscador.component.css']
})
export class BuscadorComponent implements OnInit {

  gifs: any;
  searchQuery: string = '';
  notFound = false;
  limit = 8;
  searchHistory: string[] = [];

  constructor(private service: GiphyService) { }

  ngOnInit() {
    // Recuperar el historial de búsqueda del localStorage al inicializar el componente
    const storedHistory = localStorage.getItem('searchHistory');
    if (storedHistory) {
      this.searchHistory = JSON.parse(storedHistory);
    }
  }

  searchGifs() {
    this.service.searchGifs(this.searchQuery)
      .subscribe(results => {
        this.gifs = results.data.slice(0, this.limit);
        // Agregar la búsqueda actual al historial si es única
        if (this.searchQuery.trim() !== '' && !this.searchHistory.includes(this.searchQuery)) {
          this.searchHistory.push(this.searchQuery);
          // Guardar el historial actualizado en localStorage
          localStorage.setItem('searchHistory', JSON.stringify(this.searchHistory));
        }
      }, error => {
        console.error('Error al buscar gifs:', error);
        this.notFound = true;
      });
    this.searchQuery = '';
  }

  searchFromHistory(query: string) {
    this.searchQuery = query;
    this.searchGifs();
  }
}
<p>Buscar:</p>
<input type="text" [(ngModel)]="searchQuery" placeholder="Buscar gifs...">
<button (click)="searchGifs()">Buscar</button>

<!-- Historial de búsqueda -->
<div *ngIf="searchHistory.length > 0">
  <p>Historial de búsqueda:</p>
  <ul>
    <li *ngFor="let query of searchHistory">
      <button (click)="searchFromHistory(query)">{{ query }}</button>
    </li>
  </ul>
</div>

<div class="grid-container">
  <div class="card-body" *ngFor="let gif of gifs">
    <div class="imagen">
      <img [src]="gif.images.original.url" alt="">
    </div>
    <div class="nombre">
      {{ gif.title }}
    </div>
  </div>
</div>

